SET NAMES utf8;

CREATE TABLE `champion_abilities` (
	`id` int(6) NOT NULL,
	`champion` int(6) NOT NULL,
	`name` varchar(180) NOT NULL,
	`description` text NOT NULL,
	`effect` text NOT NULL,
	`cost` varchar(180) NOT NULL,
	`range` int(4) NOT NULL,
	KEY `champion` (`champion`),
	KEY `id` (`id`)
) ENGINE=InnoDB CHARSET=utf8;

CREATE TABLE `champion_stats` (
	`champion` int(6) NOT NULL,
	`name` varchar(140) NOT NULL,
	`value` varchar(140) NOT NULL,
	`modifier` varchar(140) NOT NULL,
	KEY `champion` (`champion`)
) ENGINE=InnoDB CHARSET=utf8;

CREATE TABLE `champions` (
	`id` int(6) NOT NULL,
	`name` varchar(140) NOT NULL,
	`title` varchar(180) NOT NULL,
	`lore` text NOT NULL,
	`tags` text NOT NULL,
	`attack` int(2) NOT NULL,
	`health` int(2) NOT NULL,
	`difficulty` int(2) NOT NULL,
	`spells` int(2) NOT NULL,
	PRIMARY KEY (`id`)
) ENGINE=InnoDB CHARSET=utf8;

CREATE TABLE `items` (
	`id` int(6) NOT NULL,
	`name` varchar(140) NOT NULL,
	PRIMARY KEY (`id`)
) ENGINE=InnoDB CHARSET=utf8;

CREATE TABLE `runes` (
	`id` int(6) NOT NULL,
	`name` varchar(140) NOT NULL,
	`description` text NOT NULL,
	PRIMARY KEY (`id`)
) ENGINE=InnoDB CHARSET=utf8;

